type Parser = Parsec Void String

data PasswordPolicy
  = PasswordPolicy Int Int Char
  deriving (Show)

data Password
  = Password PasswordPolicy String
  deriving (Show)

passwordParser :: Parser Password
passwordParser = do
  digitA <- read <$> many digitChar
  char '-'
  digitB <- read <$> many digitChar
  space
  character <- asciiChar
  char ':'
  space
  Password (PasswordPolicy digitA digitB character) <$> takeRest

parsePasswords :: String -> [Password]
parsePasswords =
  fmap (fromJust . parseMaybe passwordParser) . lines

partOne :: String -> Int
partOne input = length $ filter isValid passwords
  where
    passwords = parsePasswords input
    isValid (Password (PasswordPolicy min max character) password) =
      let count = length $ filter (== character) password
       in count >= min && count <= max

partTwo :: String -> Int
partTwo input = length $ filter isValid passwords
  where
    passwords = parsePasswords input
    isValid (Password (PasswordPolicy posA posB character) password)
      | password !! (posA - 1) == character && password !! (posB - 1) /= character = True
      | password !! (posA - 1) /= character && password !! (posB - 1) == character = True
      | otherwise = False
