readGroups :: String -> [[String]]
readGroups value =
  lines <$> splitOn "\n\n" value

partOne :: String -> Int
partOne value =
  sum $ length . uniqAnswers <$> groups
  where
    groups = readGroups value
    uniqAnswers = nub . concat

partTwo :: String -> Int
partTwo value =
  sum $ length . overlapping <$> groups
  where
    groups = readGroups value
    overlapping [] = []
    overlapping [p] = p
    overlapping (p : ps) =
      let overlap person intersection = Set.intersection intersection person
       in Set.toList $ foldr overlap (Set.fromList p) (Set.fromList <$> ps)
